var toggle=document.getElementById("menuToggle"),menu=document.getElementById("menu");toggle.addEventListener("click",function(){menu.classList.contains("active")?menu.classList="":menu.classList.add("active"),console.log(menu.style.height)});var slideIndex=1;function loop(){setTimeout(function(){showSlides(slideIndex+=1),loop()},3500)}function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){var s,l=document.getElementsByClassName("mySlides"),n=document.getElementsByClassName("dot");for(e>l.length&&(slideIndex=1),e<1&&(slideIndex=l.length),s=0;s<l.length;s++)l[s].style.display="none";for(s=0;s<n.length;s++)n[s].className=n[s].className.replace(" dot-active","");l[slideIndex-1].style.display="block",n[slideIndex-1].className+=" dot-active"}showSlides(slideIndex),loop();